import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

function LoginScreen() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleLogin = async () => {
    // Aquí deberías implementar la lógica para verificar las credenciales con Firebase
    console.log('Intento de login con:', name);
    // Simulando autenticación exitosa:
    history.push('/home'); // Redirigir a Home después del login
  };

  return (
    <div className="container mt-5">
      <h2>Iniciar Sesión</h2>
      <input type="text" value={name} onChange={e => setName(e.target.value)} placeholder="Nombre" className="form-control" />
      <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Contraseña" className="form-control mt-3" />
      <button onClick={handleLogin} className="btn btn-success mt-3">Iniciar sesión</button>
      <p className="mt-3">
        ¿No tienes cuenta? <a href="/register">Regístrate aquí</a>
      </p>
    </div>
  );
}

export default LoginScreen;
