import React, { useState, useEffect } from 'react';
import { ref, onValue, getDatabase } from 'firebase/database';

function HomeScreen() {
  const [devices, setDevices] = useState([]);
  const [userId, setUserId] = useState('');
  const [selectedDevice, setSelectedDevice] = useState(null);

  useEffect(() => {
    const fetchUserId = async () => {
      try {
        const storedUserId = localStorage.getItem('userId');
        if (storedUserId) {
          setUserId(storedUserId);
          const devicesRef = ref(getDatabase(), `usuarios/${storedUserId}/devices`);
          onValue(devicesRef, (snapshot) => {
            const devicesData = snapshot.val();
            const devicesList = devicesData ? Object.keys(devicesData).map(key => ({
              id: key,
              ...devicesData[key]
            })) : [];
            setDevices(devicesList);
          });
        }
      } catch (error) {
        console.log("Error retrieving userId", error);
      }
    };

    fetchUserId();
  }, []);


  return (
    <div className='sContainer'>
      <h1>Dispositivos</h1>
      <div className='row'>
        {devices.map((item) => (

          <div className='col-6 col-md-4 col-lg-3'>
              <div className='card' key={item.id} >
              <div className='image-container d-flex justify-content-between'>
                <img
                  src={require('../assets/Icons/PottedPlant.png')}
                  width={100}
                />
                <div className='text-light fs-2'>{item.id}</div>
              </div>
            </div>
          </div>
          
        ))}
      </div>
    </div>
  );
}

export default HomeScreen;
