import React, { useState, useEffect } from 'react';
import { getDatabase, ref, query, orderByChild, equalTo, get } from 'firebase/database';
import { useNavigate } from 'react-router-dom';

function LoginScreen() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const database = getDatabase();

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      window.alert('Autenticado automáticamente.');
      navigate('/home');
    }
  }, [navigate]);

  const handleLogin = async () => {
    const usersRef = ref(database, 'usuarios');
    const userQuery = query(usersRef, orderByChild('name'), equalTo(name));

    try {
      const snapshot = await get(userQuery);
      if (snapshot.exists()) {
        let userKey = null;
        let userData = null;

        snapshot.forEach((childSnapshot) => {
          if (childSnapshot.val().pass === password) {
            userKey = childSnapshot.key;
            userData = childSnapshot.val();
          }
        });

        if (userKey && userData) {
          localStorage.setItem('userId', userKey);
          window.alert(`Inicio de sesión exitoso, Bienvenido ${userData.name}`);
          navigate('/home');
        } else {
          window.alert('Error: Contraseña incorrecta');
        }
      } else {
        window.alert('Error: Usuario no encontrado');
      }
    } catch (error) {
      console.error(error);
      window.alert('Error durante el inicio de sesión');
    }
  };

  return (
    <div style={{ marginTop: '30%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div style={{ marginBottom: '15%' }}>
        {/* Aquí puedes insertar tu logo */}
      </div>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Nombre"
        style={{ width: '80%', padding: '10px', margin: '10px', borderWidth: '1.5px', borderColor: '#68A74D', borderRadius: '5px' }}
      />
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Contraseña"
        style={{ width: '80%', padding: '10px', margin: '10px', borderWidth: '1.5px', borderColor: '#68A74D', borderRadius: '5px' }}
      />
      <button onClick={handleLogin} style={{ backgroundColor: '#68A74D', padding: '10px', borderRadius: '5px', color: '#ffffff' }}>
        Iniciar sesión
      </button>
      <button onClick={() => navigate('/register')} style={{ marginTop: '20px', color: '#68A74D', fontWeight: 'bold', fontStyle: 'italic', fontSize: '18px', textDecoration: 'underline' }}>
        ¿No tienes cuenta? Regístrate
      </button>
    </div>
  );
}

export default LoginScreen;
