import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ref, query, orderByChild, equalTo, get } from 'firebase/database';
import { database } from '../conexion/firebaseConfig'; // Asegúrate de usar la ruta correcta
import '../css/style.css';

function LoginScreen() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      window.alert('Autenticado automáticamente.');
      navigate('/home'); // O la ruta que corresponda a tu página principal
    }
  }, [navigate]);

  const handleLogin = async () => {
    const usersRef = ref(database, 'usuarios');
    const userQuery = query(usersRef, orderByChild('name'), equalTo(name));

    try {
      const snapshot = await get(userQuery);
      if (snapshot.exists()) {
        let userKey = null;
        let userData = null;
        snapshot.forEach((childSnapshot) => {
          if (childSnapshot.val().pass === password) {
            userKey = childSnapshot.key;
            userData = childSnapshot.val();
          }
        });

        if (userKey && userData) {
          localStorage.setItem('userId', userKey);
          window.alert(`Inicio de sesión exitoso, Bienvenido ${userData.name}`);
          navigate('/home');
        } else {
          window.alert('Error: Contraseña incorrecta');
        }
      } else {
        window.alert('Error: Usuario no encontrado');
      }
    } catch (error) {
      console.error(error);
      window.alert('Error durante el inicio de sesión');
    }
  };

  return (
    <div className="container mt-5">
      <div className="text-center mb-4">
        <img src={require('../assets/Icons/LogoLogin.png')} alt="Logo" className="img-fluid" style={{ width: '270px', height: '140px' }} />
      </div>
      <div className="form-group">
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Nombre"
          className="form-control"
        />
      </div>
      <div className="form-group mt-3">
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Contraseña"
          className="form-control"
        />
      </div>
      <button onClick={handleLogin} className="login-button">
        Iniciar sesión
      </button>
      <button onClick={() => navigate('/register')} className="fs-2 btn btn-link mt-3 text-green">
        ¿No tienes cuenta? Regístrate
      </button>
    </div>
  );
}

export default LoginScreen;
