import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ref, query, orderByChild, equalTo, get } from 'firebase/database';
import { database } from '../conexion/firebaseConfig'; // Asegúrate de usar la ruta correcta

function LoginScreen() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      alert('Autenticado automáticamente.');
      navigate('/home'); // o la ruta que corresponda a tu página principal
    }
  }, [navigate]);

  const handleLogin = async () => {
    const usersRef = ref(database, 'usuarios');
    const userQuery = query(usersRef, orderByChild('name'), equalTo(name));

    try {
      const snapshot = await get(userQuery);
      if (snapshot.exists()) {
        let userKey = null;
        let userData = null;
        snapshot.forEach((childSnapshot) => {
          if (childSnapshot.val().pass === password) {
            userKey = childSnapshot.key;
            userData = childSnapshot.val();
          }
        });

        if (userKey && userData) {
          localStorage.setItem('userId', userKey);
          alert(`Inicio de sesión exitoso, Bienvenido ${userData.name}`);
          navigate('/home'); // o la ruta que corresponda a tu página principal
        } else {
          alert('Error: Contraseña incorrecta');
        }
      } else {
        alert('Error: Usuario no encontrado');
      }
    } catch (error) {
      console.error(error);
      alert('Error durante el inicio de sesión');
    }
  };

  return (
    <div>
      <input
        type="text"
        value={name}
        onChange={e => setName(e.target.value)}
        placeholder="Nombre"
      />
      <input
        type="password"
        value={password}
        onChange={e => setPassword(e.target.value)}
        placeholder="Contraseña"
      />
      <button onClick={handleLogin}>Iniciar sesión</button>
    </div>
  );
}

export default LoginScreen;
