import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ref, query, orderByChild, equalTo, get } from 'firebase/database';
import { database } from '../path/to/your/firebase-config'; // Asegúrate de usar la ruta correcta

function LoginScreen() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      alert('Autenticado automáticamente.');
      navigate('/home'); // o la ruta que corresponda a tu página principal
    }
  }, [navigate]);

  const handleLogin = async () => {
    const usersRef = ref(database, 'usuarios');
    const userQuery = query(usersRef, orderByChild('name'), equalTo(name));

    try {
      const snapshot = await get(userQuery);
      if (snapshot.exists()) {
        let userKey = null;
        let userData = null;
        snapshot.forEach((childSnapshot) => {
          if (childSnapshot.val().pass === password) {
            userKey = childSnapshot.key;
            userData = childSnapshot.val();
          }
        });

        if (userKey && userData) {
          localStorage.setItem('userId', userKey);
          alert(`Inicio de sesión exitoso, Bienvenido ${userData.name}`);
          navigate('/home'); // o la ruta que corresponda a tu página principal
        } else {
          alert('Error: Contraseña incorrecta');
        }
      } else {
        alert('Error: Usuario no encontrado');
      }
    } catch (error) {
      console.error(error);
      alert('Error durante el inicio de sesión');
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.imgContainer}>
        <img src="../assets/Icons/LogoLogin.png" alt="Logo" style={styles.logo} />
      </div>
      <input
        type="text"
        value={name}
        onChange={e => setName(e.target.value)}
        placeholder="Nombre"
        style={styles.input}
      />
      <input
        type="password"
        value={password}
        onChange={e => setPassword(e.target.value)}
        placeholder="Contraseña"
        style={styles.input}
      />
      <button onClick={handleLogin} style={styles.button}>Iniciar sesión</button>
      <button onClick={onShowRegister} style={styles.registerButton}>¿No tienes cuenta? Regístrate</button>
    </div>
  );
}

const styles = {
  container: {
    flex: 1,
    marginTop: '30%',
    alignItems: 'center',
  },
  input: {
    width: '80%',
    padding: 10,
    margin: 10,
    borderWidth: 1.5,
    borderColor: '#68A74D',
    borderRadius: 5,
  },
  button: {
    backgroundColor: '#68A74D',
    padding: 10,
    borderRadius: 5,
    color: '#ffffff',
    fontSize: 15,
    marginTop: 10,
  },
  registerButton: {
    color: '#68A74D',
    fontWeight: 'bold',
    fontStyle: 'italic',
    fontSize: 18,
    textDecorationLine: 'underline',
    marginTop: 20,
  },
  imgContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: '15%',
  },
  logo: {
    width: 270,
    height: 140,
    resizeMode: 'stretch',
  },
};

export default LoginScreen;
