import React from 'react';

const DeviceModal = ({ selectedDevice, modalVisible, handleCloseModal, ledState, toggleLed }) => {
  const handleButtonClick = (e) => {
    e.stopPropagation(); // Detiene la propagación del evento de clic para que no llegue al contenedor principal de la modal
    toggleLed();
  };

  return (
    <div className={`modal ${modalVisible ? 'show' : ''}`} style={{ display: modalVisible ? 'block' : 'none' }} onClick={handleCloseModal}>
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content" onClick={(e) => e.stopPropagation()}>
          <div className="modal-header bg-green">
            <h3 className="modal-title text-light fw-bold fst-italic ms-2">{selectedDevice.id}</h3>
            <button type="button" className="btn-close" onClick={handleCloseModal}></button>
          </div>
          <div className="modal-body">
            {/* Contenido de la modal */}
            <button className={`btn ${ledState ? 'btn-danger bg-red' : 'btn-success bg-green'}`} onClick={handleButtonClick}>
              {ledState ? 'Apagar Riego' : 'Encender Riego'}
            </button>
          </div>
        </div>
      </div>
      <div className="modal-backdrop fade show"></div>
    </div>
  );
};

export default DeviceModal;
