import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ref, query, orderByChild, equalTo, get } from 'firebase/database';
import { database } from '../conexion/firebaseConfig'; // Asegúrate de usar la ruta correcta

function LoginScreen() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      window.alert('Autenticado automáticamente.');
      navigate('/home'); // o la ruta que corresponda a tu página principal
    }
  }, [navigate]);

  const handleLogin = async () => {
    const usersRef = ref(database, 'usuarios');
    const userQuery = query(usersRef, orderByChild('name'), equalTo(name));

    try {
      const snapshot = await get(userQuery);
      if (snapshot.exists()) {
        let userKey = null;
        let userData = null;
        snapshot.forEach((childSnapshot) => {
          if (childSnapshot.val().pass === password) {
            userKey = childSnapshot.key;
            userData = childSnapshot.val();
          }
        });

        if (userKey && userData) {
          localStorage.setItem('userId', userKey);
          window.alert(`Inicio de sesión exitoso, Bienvenido ${userData.name}`);
          navigate('/home'); // o la ruta que corresponda a tu página principal
        } else {
          window.alert('Error: Contraseña incorrecta');
        }
      } else {
        window.alert('Error: Usuario no encontrado');
      }
    } catch (error) {
      console.error(error);
      window.alert('Error durante el inicio de sesión');
    }
  };

  return (
    <div style={styles.container}>
        <img src={require('../assets/Icons/LogoLogin.png')} alt="Logo" style={styles.logo} />

      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Nombre"
        style={styles.input}
      />
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Contraseña"
        style={styles.input}
      />
      <button onClick={handleLogin} style={styles.button}>
        Iniciar sesión
      </button>
      <button onClick={() => navigate('/register')} style={styles.registerButton}>
        ¿No tienes cuenta? Regístrate
      </button>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '10%'
  },
  imgContainer: {
    marginBottom: '15%'
  },
  logo: {
    width: 270,
    height: 140,
    objectFit: 'contain'
  },
  input: {
    width: '20%',
    padding: '10px',
    margin: '10px',
    borderWidth: '1.5px',
    borderColor: '#68A74D',
    borderRadius: '5px'
  },
  button: {
    backgroundColor: '#68A74D',
    padding: '10px',
    borderRadius: '5px',
    color: '#ffffff',
    fontSize: '15px',
    marginTop: '10px'
  },
  registerButton: {
    marginTop: '20px',
    color: '#68A74D',
    fontWeight: 'bold',
    fontStyle: 'italic',
    fontSize: '18px',
    textDecoration: 'underline'
  }
};

export default LoginScreen;
