import React, { useState } from 'react';
import { ref, update, getDatabase } from 'firebase/database';

function ChangePasswordModal({ changePasswordModalVisible, handleChangePassword }) {
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const userId = localStorage.getItem('userId');

  const handleSaveNewPassword = async () => {
    if (newPassword !== confirmPassword) {
      setPasswordError('Las contraseñas no coinciden');
      return;
    }
    if (newPassword === '' || confirmPassword === '') {
      setPasswordError('Las contraseñas no pueden estar vacías');
      return;
    }

    const userRef = ref(getDatabase(), `usuarios/${userId}`);
    await update(userRef, { pass: newPassword });
    setNewPassword('');
    setConfirmPassword('');
    setPasswordError('');
  };

  return (
    <div className={`modal ${changePasswordModalVisible ? 'show' : ''}`} style={{ display: changePasswordModalVisible ? 'block' : 'none' }} tabIndex="-1">
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content">
          <div className="modal-header bg-green">
            <h3 className="modal-title text-light fw-bold fst-italic ms-2">Cambiar Contraseña</h3>
            <button type="button" className="btn-close" onClick={handleChangePassword}></button>
          </div>
          <div className="modal-body">
            <input 
              type="password" 
              className="form-control mb-3" 
              placeholder="Nueva Contraseña" 
              value={newPassword} 
              onChange={(e) => setNewPassword(e.target.value)} 
            />
            <input 
              type="password" 
              className="form-control mb-3" 
              placeholder="Confirmar Contraseña" 
              value={confirmPassword} 
              onChange={(e) => setConfirmPassword(e.target.value)} 
            />
            {passwordError && <div className="text-danger">{passwordError}</div>}
          </div>
          <div className="my-3 d-flex justify-content-center">
            <button className="btn btn-success bg-green" onClick={handleSaveNewPassword}>
              Guardar Contraseña
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ChangePasswordModal;
